-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `c_id` int(11) NOT NULL AUTO_INCREMENT,
  `c_username` varchar(100) DEFAULT NULL COMMENT '评论人',
  `c_comment` varchar(100) DEFAULT NULL COMMENT '评论内容',
  `c_createtime` varchar(50) DEFAULT NULL COMMENT '评论时间',
  `c_status` int(1) DEFAULT NULL COMMENT '文章状态：0已删除 1存在',
  `c_type` int(1) unsigned zerofill NOT NULL DEFAULT '0' COMMENT '类型：0文章1话题 默认为0',
  `a_id` int(11) NOT NULL COMMENT '文章Id',
  PRIMARY KEY (`c_id`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8;

