-- ----------------------------
-- Table structure for forum
-- ----------------------------
DROP TABLE IF EXISTS `forum`;
CREATE TABLE `forum` (
  `f_id` int(11) NOT NULL AUTO_INCREMENT,
  `f_title` varchar(100) DEFAULT NULL COMMENT '话题标题',
  `f_username` varchar(100) DEFAULT NULL COMMENT '话题所有者，唯一，对应user表的u_username',
  `f_createtime` varchar(50) DEFAULT NULL COMMENT '话题创建时间',
  `f_comment` int(11) DEFAULT NULL COMMENT '话题讨论数量，就是当前话题的所有讨论数',
  `f_status` int(11) DEFAULT NULL COMMENT '话题状态：0删除，1存在',
  PRIMARY KEY (`f_id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

