-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `u_id` int(11) NOT NULL AUTO_INCREMENT,
  `u_username` varchar(100) DEFAULT NULL,
  `u_password` varchar(100) DEFAULT NULL,
  `u_email` varchar(50) DEFAULT NULL,
  `u_birthday` varchar(100) DEFAULT NULL,
  `u_city` varchar(30) DEFAULT NULL COMMENT '所在地',
  `u_status` int(4) DEFAULT NULL COMMENT '用户状态。0：锁定，1：可用',
  `u_role` int(4) DEFAULT NULL COMMENT '用户角色。0：普通用户，1：管理员',
  `u_img` varchar(100) DEFAULT NULL COMMENT '用户头像路径',
  PRIMARY KEY (`u_id`)
) ENGINE=InnoDB AUTO_INCREMENT=80 DEFAULT CHARSET=utf8;

